import { Column } from 'primereact/column';

const products = [{ id: 1, name: 'Product A', price: 100 }];

function ProductsTable() {
  return (
    <DataTable value={products} paginator rows={10}>
      <Column field="name" header="Name" sortable />
      <Column field="price" header="Price" />
    </DataTable>
  );
}

form_example.code:
import { InputText } from 'primereact/inputtext';
import { Dropdown } from 'primereact/dropdown';
import { Calendar } from 'primereact/calendar';
import { Button } from 'primereact/button';

function UserForm() {
  const [name, setName] = useState('');
  const [city, setCity] = useState(null);
  const [date, setDate] = useState(null);
  const cities = [{ name: 'New York', code: 'NY' }, { name: 'London', code: 'LDN' }];

  return (
    <form>
      <InputText value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" />
      <Dropdown value={city} options={cities} onChange={(e) => setCity(e.value)} optionLabel="name" placeholder="Select City" />
      <Calendar value={date} onChange={(e) => setDate(e.value)} showIcon />
      <Button label="Submit" type="submit" />
    </form>
  );
}

toast_notification.code:
import { Toast } from 'primereact/toast';
import { useRef } from 'react';

function MyComponent() {
  const toast = useRef(null);

  const showSuccess = () => {
    toast.current.show({ severity: 'success', summary: 'Success', detail: 'Operation completed' });
  };

  return (
    <div>
      <Button label="Show Toast" onClick={showSuccess} />
      <Toast ref={toast} />
    </div>
  );
}
