generic_function.code:
function identity<T>(arg: T): T {
  return arg;
}

const result = identity<string>('Hello');

type_guard.code:
interface Fish {
  swim(): void;
}

interface Bird {
  fly(): void;
}

function isFish(pet: Fish | Bird): pet is Fish {
  return (pet as Fish).swim !== undefined;
}
